import React from 'react'

const FilterAuthor = ({defaultValue, posts}) => {
    return (
        <select>
            <option disabled value="" placeholder='Фильтр по Автору'>{defaultValue}</option>
        {posts.map(post => 
            <option >{post.author}</option>
         )}
      </select>
    );
}

export default FilterAuthor;
