import React, {useState} from "react";
import PostItem from "./components/PostItems.jsx";
import "./style.css/App.css";

function App() {
  const [posts, setPosts] = React.useState([
    {id:1, tittle:"Заголовок1", body:"Description1", subject:"1", author:"1", date:"1" },
    {id:2, tittle:"Заголовок2", body:"Description2", subject:"2", author:"2", date:"2" },
  ]);
  return (
    <div className="App">
      {posts.map(post=>
       <PostItem post={post} key={post.id}/>)}
      <button className="btn__add">Добавить статью</button>
    </div>
  )
}

export default App;
