import React from 'react'

const FilterAuthor = ({options, defaultValue, posts}) => {
    return (
        <select>
            <option value={defaultValue}>{defaultValue}</option>
        {posts.map(post => 
            <option >{post.author}</option>
         )}
      </select>
    );
}

export default FilterAuthor;
