import React, {useState} from "react";
import PostItem from "./components/PostItems.jsx";
import "./style.css/App.css";

function App() {
  const [posts, setPosts] = React.useState([
    {id:1, tittle:"Заголовок1", body:"Description1", subject:"s1", author:"a1", date:"d1" },
    {id:2, tittle:"Заголовок2", body:"Description2", subject:"s2", author:"a2", date:"d2" },
  ]);
  return (
    <div className="App">
      <h1>Список постов</h1>
      {posts.map(post=>
       <PostItem post={post} key={post.id}/>)}
      <button className="btn__add">Добавить статью</button>
    </div>
  )
}

export default App;
