import React from 'react'

function FilterAuthor(posts) {
    const SelectAuthor =() => {
        <select>
        {posts.map(post => 
            <option >{post.author}</option>
         )}
      </select>
    }
  return (
    <div>      
      {SelectAuthor(posts)}
    </div>
  )
}

export default FilterAuthor;
